inline bool read()
{
    if(!(cin >> n >> m)) return false;
    forn(i, m)
    {
        int u, v;
        assert(cin >> u >> v);
        g[u][v] = g[v][u] = true;
        deg[u]++, deg[v]++;
    }

    return true;
}

inline void solve()
{
    int ans = INF;
    forn1(i, n)
    {
        for(int j = i + 1; j <= n; j++)
        {
            if(!g[i][j]) continue;
            for(int k = 1; k <= n; k++)
            {
                if(k == i || k == j) continue;
                if(!g[i][k] || !g[j][k]) continue;
                int cur = deg[i] + deg[j] + deg[k] - 6;
                ans = min(ans, cur);
            }
        }
    }

    if(ans > INF / 2) ans = -1;
    cout << ans << endl;
    return;
}

