const int MAXN = 1e4+5;

bool adj[MAXN][MAXN];
vector<vector<int>> g;

void solve()
{
    int n, m; cin >> n >> m;
    g.resize(n+5);

    for(int i = 0; i < MAXN; ++i)
        for(int j = 0; j < MAXN; ++j)
            adj[i][j] = false;
    for(int i = 0; i < m; ++i) {
        int a, b; cin >> a >> b;
        adj[a][b] = adj[b][a] = true;
        g[a].pb(b);
        g[b].pb(a);
    }
    int ans = INF;
    for(int i = 1; i <= n; ++i) {
        if((int)g[i].size()-2 >= ans)
            continue;
        for(int k = 0; k < (int)g[i].size(); ++k) {
            for(int j = 0; j < (int)g[i].size(); ++j) {
                if(i == j)
                    continue;
                if(!adj[g[i][j]][g[i][k]])
                    continue;
                int to1 = g[i][j], to2 = g[i][k];
                if(ans > g[i].size()-2 + g[to1].size()-2 + g[to2].size()-2)
                    ans = g[i].size()-2 + g[to1].size()-2 + g[to2].size()-2;
            }
        }
    }
    cout << (ans == INF ? -1 : ans);


}

