import java.io.*;
import java.util.*;

public class PrB {
 public static void main(String[] args) throws IOException {
  new PrB().run();
 }

 BufferedReader in;
 PrintWriter out;
 StringTokenizer st;

 String nextToken() throws IOException {
  while (st == null || !st.hasMoreTokens()) {
   st = new StringTokenizer(in.readLine());
  }
  return st.nextToken();
 }

 int nextInt() throws IOException {
  return Integer.parseInt(nextToken());
 }

 void run() throws IOException {
  in = new BufferedReader(new InputStreamReader(System.in));
  out = new PrintWriter(System.out, true);
  solve();
  out.flush();
 }

 void solve() throws IOException {
  int n = nextInt();
  int m = nextInt();
  int[] num = new int[n];
  HashSet<Integer>[] ed = new HashSet[n];
  for (int i = 0; i < n; i++) {
   ed[i] = new HashSet<Integer>();
  }
  for (int i = 0; i < m; i++) {
   int u = nextInt() - 1;
   int v = nextInt() - 1;
   ed[u].add(v);
   ed[v].add(u);
   num[u]++;
   num[v]++;
  }
  int best = -1;
  for (int i = 0; i < n; i++) {
   for (int j : ed[i]) {
    if (i < j) {
     for (int k : ed[j]) {
      if (j < k && ed[i].contains(k)) {
       if (best == -1 || best > num[i] + num[j] + num[k] - 6) {
        best = num[i] + num[j] + num[k] - 6;
       }
      }
     }
    }
   }
  }
  out.println(best);
 }
}

