int n, m;
int aa, bb;
int ans = -1;
int main(){
    cin >> n >> m;
    for(int i = 1; i <= m; ++i){
        cin >> aa >> bb;
        if(aa > bb)
            swap(aa,bb);
        g[aa].push_back(bb);
        a[aa][bb] = 1;
        a[bb][aa] = 1;
        z[aa] ++;
        z[bb] ++;
    }
    for(int i = 1; i <= n; ++i){
        for(int j = 0; j < g[i].size(); ++j)
