        vector <int> G[4005];
        for(int i = 1; i <= m; i++) {
            int u = read(), v = read();
            mp[u][v] = mp[v][u] = 1;
            G[u].push_back(v);
            G[v].push_back(u);
        }
        int ans = -1;
        for(int i = 1; i <= n && ans; i++) {
            for(int j = i + 1; j <= n && ans; j++) {
                if(mp[i][j]) {
                    for(int k = j + 1; k <= n && ans; k++) {
                        if(mp[j][k] && mp[i][k]) {
                            if(ans == -1 || ans > G[i].size()-1 + G[j].size()-1 + G[k].size()-1) {
                                ans = G[i].size()-2 + G[j].size()-2 + G[k].size()-2;
                            }
                        }
                    }
                }
            }
        }
        printf("%d\n", ans);
    }

    return 0;
}

#include <bits/stdc++.h>
#define pb push_back
using namespace std;
const int maxn = 4001;
vector <int> adj[maxn];
int ma[maxn][maxn];
int main()
{
    //freopen("in.txt", "r", stdin);
    ios_base::sync_with_stdio(0);
    cin.tie(0);
    int n, m, a, b, ans = 1e9, tmp;
    cin >> n >> m;
    for(int i = 0; i < m; i++)
    {
        cin >> a >> b;
        adj[a].pb(b);
        adj[b].pb(a);
        ma[a][b] = ma[b][a] = 1;
    }
    for(int i = 1; i <= n; i++)
    {
        tmp = 0;
        for(int j = 0; j < adj[i].size(); j++)
        {
            for(int k = j + 1; k < adj[i].size(); k++)
            {
                int x = adj[i][j], y = adj[i][k];
                if(ma[x][y])
                {
                    tmp = adj[i].size() + adj[x].size() + adj[y].size() - 6;
                    ans = min(tmp, ans);
                }
            }
        }
    }
    if(ans != 1e9)
        cout << ans;
    else
        cout << -1;
    return 0;
}

